        if isinstance(unit, np.int64) == True:
            SV1_04 = str(int(unit))
        else:
            SV1_04 = str(unit)

        LX1 = ["LX", "1"]
        SV1 = ["SV1", SV1_01, str(amount), "UN", SV1_04, "", "", "1", "", "", "", "", "", "", "", "0"]
        DTP = ["DTP", "472", "D8", service_date]

        return '*'.join(LX1) + "~" + '*'.join(SV1) + "~" + '*'.join(DTP) + "~"

